type Constant struct {
	current    string
	capePath   string
	optifineIp string
}

var optifineServer = "s.optifine.net"

func main() {
	r := mux.NewRouter()
	listener := listen(0)
	cnst := inits()

	r.PathPrefix("/capes/{cape}").HandlerFunc(cnst.handleCapes)
	r.PathPrefix("/").HandlerFunc(cnst.handleOther)

	fmt.Printf("Optifine Cape Proxy is listening at %s\n\n", listener.Addr().String())
	handleHostsFile(strings.Replace(listener.Addr().String(), ":80", "", -1))
	http.Handle("/", r)
	log.Fatal(http.Serve(listener, nil))
}

