func handleHostsFile(ip string) {
	hostsRaw, _ := hostsfile.ReadHostsFile()
	hosts, _ := hostsfile.ParseHosts(hostsfile.ReadHostsFile())
	info, _ := os.Stat(hostsfile.HostsPath)
	keys := make(map[string]string, 0)
	hostsStr := string(hostsRaw)

	for k, v := range hosts {
		for _, s := range v {
			keys[s] = k
		}
	}

	aOptifineSrv := fmt.Sprintf("%s %s", ip, optifineServer)

	if keys[optifineServer] == ip {
		return
	}

	if keys[optifineServer] == "" {
		hostsStr = fmt.Sprintf("%s\n%s", hostsStr, aOptifineSrv)
	} else {
		matcher, _ := regexp.Compile("((25[0-5]|(2[0-4]|1\\d|[1-9]|)\\d)\\.?\\b){4} *" + optifineServer)
		hostsStr = strings.Replace(hostsStr, matcher.FindString(hostsStr), aOptifineSrv, -1)
	}

	err := os.WriteFile(hostsfile.HostsPath, []byte(hostsStr), info.Mode())
	if errors.Is(err, fs.ErrPermission) {
		if keys[optifineServer] == "" {
			fmt.Printf("Application don't have the permission to modify hosts file\neither re-run with the appropriate permission or\nmanually add this to the hosts file\n\n	%s", aOptifineSrv)
		} else {
			fmt.Printf("Application don't have the permission to modify hosts file\neither re-run with the appropriate permission or\nreplace this\n\n	%s\n\nto this in the hosts file\n\n	%s", keys[optifineServer]+" "+optifineServer, aOptifineSrv)
		}
	}
}

func listen(iteration int) net.Listener {
	iteration++
	listener, err := net.Listen("tcp", fmt.Sprintf("127.0.0.%d:80", iteration))

	if err != nil {
		if errors.Is(err, os.ErrPermission) {
			log.Fatal(err)
		}
		return listen(iteration)
	}

	return listener
}

func (dir *Constant) handleCapes(w http.ResponseWriter, r *http.Request) {
	capeStr := mux.Vars(r)["cape"]
	capePath := path.Join(dir.capePath, capeStr)

	if !strings.Contains(capeStr, ".png") {
		dir.handleOther(w, r)
		return
	}

	cape, err := os.ReadFile(capePath)
	if errors.Is(err, fs.ErrNotExist) {
		dir.handleOther(w, r)
		return
	}

	fmt.Printf("Requesting cape \"%s\"\n", capeStr)

	w.Write(cape)
}

func (dir *Constant) handleOther(w http.ResponseWriter, r *http.Request) {
	resp, err := http.Get(fmt.Sprintf("http://%s/%s", dir.optifineIp, r.URL.Path))
	if err != nil {
		log.Fatal(err)
	}

	body, _ := io.ReadAll(resp.Body)
	w.Write(body)
}
